-- Create "trips" table
CREATE TABLE `trips` (`id` bigint NOT NULL AUTO_INCREMENT, `created_at` timestamp NOT NULL, `updated_at` timestamp NOT NULL, `drive_id` bigint NULL, `start_x` double NOT NULL, `start_y` double NOT NULL, `end_x` double NOT NULL, `end_y` double NOT NULL, `price` double NOT NULL, `status` enum('pending','accept','done','cancel') NOT NULL DEFAULT "pending", `rate` bigint NULL, `user_id` bigint NOT NULL, PRIMARY KEY (`id`), INDEX `trips_users_trips` (`user_id`), CONSTRAINT `trips_users_trips` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON UPDATE NO ACTION ON DELETE NO ACTION) CHARSET utf8mb4 COLLATE utf8mb4_bin;
